stack.post:
ludicrous-server=&Kickstart_PrivateAddress;
grep boss /proc/cmdline 2>&1 /dev/null if [ $? -eq 0 ]; then ludicrous-server=$(sed -r -n 's/.* stacki.pxeserver=([.[:alnum:]]*) .*/\1/p' /proc/cmdline;) fi
curl_cmd=/usr/bin/curl
if [ -f /bin/curl ]; then curl_cmd=/bin/curl fi
peerdone_url=https://${ludicrous-server}/avalanche/peerdone ${curl_cmd} --insecure --output /dev/null ${peerdone_url}
