stack.native:
unsupported_hardware
install

%include /tmp/partition-info

lang &Kickstart_Lang;
keyboard &Kickstart_Keyboard;

timezone --utc &Kickstart_Timezone;


if '&boot_device;' != '' and '&release;' == '6.x':
	print "bootloader --location=none"
elif '&boot_device;' != '' and '&release;' == '7.x':
	print "bootloader --location=mbr --boot-drive=&boot_device;"
else:
	print "bootloader --location=mbr"

post.file:
/* Turn off escape character parsing. This keeps tabs and newlines intact. */ $EscapeControlCharactersOnReceive off
# Send local0 facility to /var/log/local0 local0.* /var/log/local0
/* If we're calling this from StackI command line, stop processing now */ if $programname == 'SCL' then ~
