stack.post:
cluster_kickstart=/boot/kickstart/cluster-kickstart
if [ -f $cluster_kickstart ]; then
        chgrp wheel	$cluster_kickstart
        chmod u+s 	$cluster_kickstart
        chmod g+x	$cluster_kickstart
fi



(
	cd /var/www/html ;
	ln -s /export/stack install;
	cd install/sbin ;
	ln -s . public ;
)
