main.eval:
import stack.api

#
# output "url" line first -- the url line will be a path to 'stacki'
#
listpallet = stack.api.Call('list pallet')

for o in listpallet:
	boxes = o['boxes'].split()
	if '&box;' in boxes and o['name'] == 'stacki':
		print '<url> --url http://127.0.0.1/install/pallets/%s/%s/redhat/%s</url>' % (o['name'], o['version'], o['arch'])

#
# now output the repos
#
for o in listpallet:
	boxes = o['boxes'].split()
	if '&box;' in boxes:
		print '<repo> --name="%s" --baseurl=http://127.0.0.1/install/pallets/%s/%s/redhat/%s</repo>' % (o['name'], o['name'], o['version'], o['arch'])

for o in stack.api.Call('list cart'):
	boxes = o['boxes'].split()
	if '&box;' in boxes:
		print '<repo> --name="%s" --baseurl=http://127.0.0.1/install/carts/%s</repo>' % (o['name'], o['name'])
