kickstart.pre:
import sys sys.path.append('/opt/stack/lib')
import stack_partition import os
nukedisks = '&nukedisks;'
def initDiskLabel(disk): # break down software disk arrays cmd = '/sbin/mdadm --stop --scan > /dev/null 2>&1' os.system(cmd)
cmd = '/sbin/mdadm --zero-superblock /dev/%s' % disk cmd += ' > /dev/null 2>&1' os.system(cmd)
# Clear out the master boot record of the drive cmd = '/bin/dd if=/dev/zero of=/dev/%s count=512 bs=1' % disk cmd += ' > /dev/null 2>&1' os.system(cmd)
cmd = '/usr/sbin/parted -s /dev/%s mklabel gpt' % disk cmd += ' > /dev/null 2>&1' os.system(cmd)
def getdisklist(s): if s.upper() in ['ALL','TRUE','Y','YES']: p = stack_partition.StackPartition() return p.getDisks() if s.upper() in ['NONE','FALSE','N','NO']: return []
return s.split()
disks = getdisklist(nukedisks)
for disk in disks: initDiskLabel(disk)
