stack:stack.stack:script:
#!/bin/sh # ### BEGIN INIT INFO # Provides: stacki-iptables # Required-Start: $network # Required-Stop: $network # Default-Start: 3 5 # Default-Stop: 0 1 2 6 # Short-Description: Apply iptables rules # Description: Apply iptables rules to a host ### END INIT INFO #
case "$1" in start) echo "Applying iptables rules" cat /etc/sysconfig/iptables | /usr/sbin/iptables-restore ;;
stop) /usr/sbin/iptables -F ;;
*) echo "Usage: $0 {start|stop}" exit 1 esac
exit 0
/sbin/chkconfig --add stacki-iptables
